	console.log(1);
	//reject(2)
	resolve(2)
	console.log(3)
	setTimeout(()=>{console.log(4)},0)
}).then(res=>{
	console.log(res)
	return new MyPromise((resolve,reject)=>{
		resolve(5)
	}).then(res=>{
		return res
	})
}).then(res=>{
	console.log(res)
}).catch(e=>{
